import React, { useEffect, useRef, useState } from 'react';
import "./experience.css";

const Experience = () => {
    const [isVisible, setIsVisible] = useState(false);
    const experienceRef = useRef(null);

    const handleScroll = () => {
        if (experienceRef.current) {
            const rect = experienceRef.current.getBoundingClientRect();
            const isInView = rect.top <= window.innerHeight && rect.bottom >= 0;

            if (isInView) {
                setIsVisible(true);
            } else {
                setIsVisible(false);
            }
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        handleScroll(); // Trigger on mount to check initial position
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div id="experience" className="experience-page">
            <h2 id="experience-header">Experience</h2>
            <div ref={experienceRef} className={isVisible ? "experiences visible" : "experiences"}>
                {/* Your dropdown components */}
            </div>
        </div>
    );
};

export default Experience;
