import React, { useEffect, useRef, useState } from 'react';
import "./aboutme.css";

const AboutMe = () => {
    const [isVisible, setIsVisible] = useState(false);
    const aboutRef = useRef(null);

    const handleScroll = () => {
        if (aboutRef.current) {
            const topPos = aboutRef.current.getBoundingClientRect().top;
            const onScreen = topPos < window.innerHeight;
            setIsVisible(onScreen);
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div id="about" className="about-page">
            <h2 id="aboutheader">About Me</h2>
            <div className={isVisible ? "background-container visible" : "background-container"} ref={aboutRef}>
                <div className={isVisible ? "aboutme visible" : "aboutme"}>
                    <p>
                        I'm Kinton Duong, a student at UC Berkeley majoring in Computer Science and Data Science interested in the intersection between tech and human cognition. 
                        Outside of school, I love to make beats, watch sports, and try new foods. A big part of my life growing up was playing water polo, and even though I am not
                        playing competitively anymore, the grit, leadership, and dedication will always be instilled in me. Feel free to click on the pages to learn more about my technical experiences and interests.
                    </p>
                </div>
            </div>
        </div>
    );
};

export default AboutMe;
