import React, { useEffect, useRef, useState } from 'react';
import "./experience.css";

const Experience = () => {
    const [isVisible, setIsVisible] = useState(false);
    const experienceRef = useRef(null);

    const handleScroll = () => {
        if (experienceRef.current) {
            const rect = experienceRef.current.getBoundingClientRect();
            const isInView = rect.top <= window.innerHeight && rect.bottom >= 0;

            if (isInView) {
                setIsVisible(true);
            } else {
                setIsVisible(false);
            }
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        handleScroll(); // Check immediately on mount
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div id="experience" className="experience-page">
            <h2 id="experience-header">Experience</h2>
            <div ref={experienceRef} className={isVisible ? "experiences visible" : "experiences"}>
                {/* Existing dropdown content */}
                <div className="dropdown">
                    <div className="dropdown-header">Contract Software Engineer | Meta </div>
                    <div className="dropdown-content">
                    <ul> 
                            <li style={{ fontSize: '1.7vh' }} >  ● Currently training various ML models to predict optimal block partitioning for video encoding to reduce computational demand</li>
                            <li style={{ fontSize: '1.7vh' }}>  ● Integrating custom ML predictions into the SVT-AV1 encoder, modifying open-source code for enhanced performance</li>
                            <li style={{ fontSize: '1.7vh' }}>  ● Conducting testing and analysis to evaluate partitioning strategies against standard encoding benchmarks to maintain a 6% quality drop threshold</li>
                        </ul>
                    </div>
                </div>
                <div className="dropdown">
                    <div className="dropdown-header">Contract Software Engineer | Voices for Children</div>
                    <div className="dropdown-content">
                        <ul>
                            <li>  ● Built a full-stack web app for non-profit to streamline community partnerships and manage in-kind donations</li>
                            <li>  ● Designed a user-friendly admin interface using React.js and Figma to create an aesthetic inventory collection database</li>
                            <li>  ● Implemented GET and POST routes for API servers, efficiently storing and retrieving data with PostgreSQL and Express.js</li>
                        </ul>
                    </div>
                </div>
                <div className="dropdown">
                    <div className="dropdown-header">Business Consultant | Adobe</div>
                    <div className="dropdown-content">
                        <ul>
                            <li>  ● Built a full-stack web app for non-profit to streamline community partnerships and manage in-kind donations</li>
                            <li>  ● Designed a user-friendly admin interface using React.js and Figma to create an aesthetic inventory collection database</li>
                            <li>  ● Implemented GET and POST routes for API servers, efficiently storing and retrieving data with PostgreSQL and Express.js</li>
                        </ul>
                    </div>
                </div>
                <div className="dropdown">
                    <div className="dropdown-header">Contract Software Engineer | Voices for Children</div>
                    <div className="dropdown-content">
                        <ul>
                            <li>  ● Built a full-stack web app for non-profit to streamline community partnerships and manage in-kind donations</li>
                            <li>  ● Designed a user-friendly admin interface using React.js and Figma to create an aesthetic inventory collection database</li>
                            <li>  ● Implemented GET and POST routes for API servers, efficiently storing and retrieving data with PostgreSQL and Express.js</li>
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Experience;
